'use client';

import { useEffect, useMemo, useState } from 'react';
import { useTheme } from '@/app/components/ThemeProvider';
import type { ContentItem, Project } from '@/lib/types';
import { USER_DATA } from '@/lib/data';
import { applyContentToUserData } from '@/lib/content';
import { Navbar } from '@/app/components/Navbar';
import { Footer } from '@/app/components/Footer';
import { useContent } from '@/app/hooks/useContent';

const pageOptions = ['global', 'profile', 'home', 'about', 'projects', 'contact'];

export default function AdminPage() {
  const { theme: t } = useTheme();
  const { content } = useContent();
  const userData = applyContentToUserData(USER_DATA, content);

  // Content State
  const [items, setItems] = useState<ContentItem[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isAuthenticated, setIsAuthenticated] = useState<boolean | null>(null);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [filter, setFilter] = useState('all');
  const [form, setForm] = useState({ pageSlug: 'home', key: '', value: '' });
  const [editingId, setEditingId] = useState<number | null>(null);
  const [editForm, setEditForm] = useState({ pageSlug: '', key: '', value: '' });

  // Project State
  const [projects, setProjects] = useState<Project[]>([]);
  const [projectForm, setProjectForm] = useState({
    title: '',
    description: '',
    imageUrl: '',
    demoUrl: '',
    repoUrl: '',
    techStack: '',
    featured: false,
  });
  const [editingProjectId, setEditingProjectId] = useState<number | null>(null);

  const filteredItems = useMemo(() => {
    if (filter === 'all') {
      return items;
    }
    return items.filter(item => item.pageSlug === filter);
  }, [filter, items]);

  const loadItems = async () => {
    try {
      setIsLoading(true);
      setError(null);
      const response = await fetch('/api/content', { cache: 'no-store' });
      if (!response.ok) {
        throw new Error('Failed to load content');
      }
      const data = (await response.json()) as { items: ContentItem[] };
      setItems(data.items ?? []);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load content');
    } finally {
      setIsLoading(false);
    }
  };

  const loadProjects = async () => {
    try {
      const response = await fetch('/api/projects', { cache: 'no-store' });
      if (response.ok) {
        const data = await response.json();
        setProjects(data.projects ?? []);
      }
    } catch (error) {
      console.error('Failed to load projects', error);
    }
  };

  const loadSession = async () => {
    try {
      const response = await fetch('/api/admin/session', { cache: 'no-store' });
      const data = (await response.json()) as { authenticated?: boolean };
      setIsAuthenticated(Boolean(data.authenticated));
    } catch (err) {
      setIsAuthenticated(false);
    }
  };

  useEffect(() => {
    loadSession();
  }, []);

  useEffect(() => {
    if (isAuthenticated) {
      loadItems();
      loadProjects();
    }
  }, [isAuthenticated]);

  const handleLogin = async () => {
    setError(null);
    const response = await fetch('/api/admin/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(loginForm),
    });

    if (!response.ok) {
      setError('Invalid admin credentials.');
      return;
    }

    setLoginForm({ username: '', password: '' });
    setIsAuthenticated(true);
  };

  const handleLogout = async () => {
    await fetch('/api/admin/logout', { method: 'POST' });
    setIsAuthenticated(false);
    setItems([]);
    setProjects([]);
  };

  // Content Handlers
  const handleCreate = async () => {
    if (!form.pageSlug.trim() || !form.key.trim() || !form.value.trim()) {
      setError('All fields are required to create content.');
      return;
    }
    const response = await fetch('/api/content', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    });
    if (!response.ok) {
      setError('Failed to create content.');
      return;
    }
    setForm({ pageSlug: 'home', key: '', value: '' });
    await loadItems();
  };

  const handleEditStart = (item: ContentItem) => {
    setEditingId(item.id);
    setEditForm({ pageSlug: item.pageSlug, key: item.key, value: item.value });
  };

  const handleEditSave = async () => {
    if (!editingId) return;
    const response = await fetch(`/api/content/${editingId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editForm),
    });
    if (!response.ok) {
      setError('Failed to update content.');
      return;
    }
    setEditingId(null);
    await loadItems();
  };

  const handleDelete = async (id: number) => {
    const response = await fetch(`/api/content/${id}`, { method: 'DELETE' });
    if (!response.ok) {
      setError('Failed to delete content.');
      return;
    }
    await loadItems();
  };

  // Project Handlers
  const handleCreateProject = async () => {
    const techStackArray = projectForm.techStack.split(',').map(s => s.trim()).filter(Boolean);
    const response = await fetch('/api/projects', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...projectForm, techStack: techStackArray }),
    });

    if (response.ok) {
      setProjectForm({ title: '', description: '', imageUrl: '', demoUrl: '', repoUrl: '', techStack: '', featured: false });
      loadProjects();
    } else {
      setError('Failed to create project');
    }
  };

  const handleUpdateProject = async () => {
    if (!editingProjectId) return;
    const techStackArray = projectForm.techStack.split(',').map(s => s.trim()).filter(Boolean);
    const response = await fetch(`/api/projects/${editingProjectId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...projectForm, techStack: techStackArray }),
    });

    if (response.ok) {
      setEditingProjectId(null);
      setProjectForm({ title: '', description: '', imageUrl: '', demoUrl: '', repoUrl: '', techStack: '', featured: false });
      loadProjects();
    } else {
      setError('Failed to update project');
    }
  };

  const handleDeleteProject = async (id: number) => {
    if (confirm('Are you sure you want to delete this project?')) {
      const response = await fetch(`/api/projects/${id}`, { method: 'DELETE' });
      if (response.ok) {
        loadProjects();
      } else {
        setError('Failed to delete project');
      }
    }
  };

  const startEditProject = (project: Project) => {
    setEditingProjectId(project.id!);
    setProjectForm({
      title: project.title,
      description: project.description,
      imageUrl: project.imageUrl || '',
      demoUrl: project.demoUrl || '',
      repoUrl: project.repoUrl || '',
      techStack: project.techStack.join(', '),
      featured: false, // Type definition for Project might miss this if not updated, but API sends it
    });
  };

  const cancelEditProject = () => {
    setEditingProjectId(null);
    setProjectForm({ title: '', description: '', imageUrl: '', demoUrl: '', repoUrl: '', techStack: '', featured: false });
  };


  if (isAuthenticated === null) {
    return (
      <div className={`min-h-screen transition-colors duration-500 ${t.colors.bg}`}>
        <Navbar content={content} />

        <main className="max-w-2xl mx-auto px-6 md:px-8 py-16 pb-28">
          <div className="animate-fade-in-up" style={{ animationDelay: '100ms' }}>
            <h1 className={`text-3xl md:text-4xl font-bold mb-4 ${t.colors.highlight}`}>Loading admin session…</h1>
            <p className={t.colors.subtext}>Checking credentials and preparing the admin panel.</p>
          </div>
        </main>

        <Footer data={userData} theme={t} content={content} />
      </div>
    );
  }

  if (isAuthenticated === false) {
    return (
      <div className={`min-h-screen transition-colors duration-500 ${t.colors.bg}`}>
        <Navbar content={content} />

        <main className="max-w-2xl mx-auto px-6 md:px-8 py-16 pb-28">
          <div className="animate-fade-in-up" style={{ animationDelay: '100ms' }}>
            <h1 className={`text-4xl md:text-5xl font-extrabold tracking-tight mb-4 ${t.colors.highlight}`}>
              Admin Login
            </h1>
            <p className={`text-lg mb-10 ${t.colors.subtext}`}>
              Sign in to access the content management panel.
            </p>

            {error && (
              <div className={`mb-6 p-4 rounded-xl border ${t.colors.border} ${t.colors.surface}`}>
                <p className={`text-sm ${t.colors.highlight}`}>{error}</p>
              </div>
            )}

            <section className={`p-6 rounded-2xl border ${t.colors.border} ${t.colors.surface}`}>
              <div className="grid grid-cols-1 gap-4">
                <input
                  value={loginForm.username}
                  onChange={event => setLoginForm(prev => ({ ...prev, username: event.target.value }))}
                  placeholder="Username"
                  className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
                />
                <input
                  type="password"
                  value={loginForm.password}
                  onChange={event => setLoginForm(prev => ({ ...prev, password: event.target.value }))}
                  placeholder="Password"
                  className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
                />
                <button
                  type="button"
                  onClick={handleLogin}
                  className={`px-4 py-2 rounded-lg font-bold ${t.colors.accent === 'text-[#89b4fa]' ? 'bg-[#89b4fa] text-[#1e1e2e]' : 'bg-[#1e66f5] text-white'}`}
                >
                  Sign in
                </button>
              </div>
            </section>
          </div>
        </main>

        <Footer data={userData} theme={t} content={content} />
      </div>
    );
  }

  return (
    <div className={`min-h-screen transition-colors duration-500 ${t.colors.bg}`}>
      <Navbar content={content} />

      <main className="max-w-6xl mx-auto px-6 md:px-8 py-16 pb-28">
        <div className="animate-fade-in-up" style={{ animationDelay: '100ms' }}>
          <h1 className={`text-4xl md:text-5xl font-extrabold tracking-tight mb-4 ${t.colors.highlight}`}>Admin Panel</h1>
          <p className={`text-lg mb-10 ${t.colors.subtext}`}>Manage the text slugs that power your public pages.</p>

          {error && (
            <div className={`mb-6 p-4 rounded-xl border ${t.colors.border} ${t.colors.surface}`}>
              <p className={`text-sm ${t.colors.highlight}`}>{error}</p>
            </div>
          )}

          <section className={`p-6 rounded-2xl border ${t.colors.border} ${t.colors.surface} mb-12`}>
            <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
              <div>
                <h2 className={`text-2xl font-bold ${t.colors.highlight}`}>Manage Projects</h2>
                <p className={t.colors.subtext}>Add or update your portfolio projects.</p>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              <input
                value={projectForm.title}
                onChange={e => setProjectForm(prev => ({ ...prev, title: e.target.value }))}
                placeholder="Project Title"
                className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
              />
              <input
                value={projectForm.description}
                onChange={e => setProjectForm(prev => ({ ...prev, description: e.target.value }))}
                placeholder="Description"
                className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
              />
              <input
                value={projectForm.imageUrl}
                onChange={e => setProjectForm(prev => ({ ...prev, imageUrl: e.target.value }))}
                placeholder="Image URL"
                className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
              />
              <input
                value={projectForm.demoUrl}
                onChange={e => setProjectForm(prev => ({ ...prev, demoUrl: e.target.value }))}
                placeholder="Demo URL"
                className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
              />
              <input
                value={projectForm.repoUrl}
                onChange={e => setProjectForm(prev => ({ ...prev, repoUrl: e.target.value }))}
                placeholder="Repo URL"
                className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
              />
              <input
                value={projectForm.techStack}
                onChange={e => setProjectForm(prev => ({ ...prev, techStack: e.target.value }))}
                placeholder="Tech Stack (comma separated)"
                className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
              />
              <div className="flex items-center gap-2">
                <input
                  type="checkbox"
                  id="featured-checkbox"
                  checked={projectForm.featured}
                  onChange={e => setProjectForm(prev => ({ ...prev, featured: e.target.checked }))}
                  className="w-5 h-5"
                />
                <label htmlFor="featured-checkbox" className={`${t.colors.text}`}>Featured Project</label>
              </div>
            </div>
            <div className="flex justify-end gap-2">
              {editingProjectId && (
                <button
                  type="button"
                  onClick={cancelEditProject}
                  className={`px-4 py-2 rounded-lg border ${t.colors.border} ${t.colors.text}`}
                >
                  Cancel
                </button>
              )}
              <button
                type="button"
                onClick={editingProjectId ? handleUpdateProject : handleCreateProject}
                className={`px-4 py-2 rounded-lg font-bold ${t.colors.accent === 'text-[#89b4fa]' ? 'bg-[#89b4fa] text-[#1e1e2e]' : 'bg-[#1e66f5] text-white'}`}
              >
                {editingProjectId ? 'Update Project' : 'Add Project'}
              </button>
            </div>

            <div className="mt-8 space-y-4">
              {projects.map(project => (
                <div key={project.id} className={`p-4 rounded-xl border ${t.colors.border} ${t.colors.surfaceHighlight} flex justify-between items-center`}>
                  <div>
                    <div className="flex items-center gap-2">
                      <h3 className={`font-bold ${t.colors.text}`}>{project.title}</h3>
                      {/* {project.featured && <span className="text-yellow-500 text-xs font-bold border border-yellow-500 px-1 rounded">FEATURED</span>} */}
                    </div>
                    <p className={`text-sm ${t.colors.subtext}`}>{project.description}</p>
                    <p className={`text-xs ${t.colors.subtext} mt-1`}>{project.techStack.join(', ')}</p>
                  </div>
                  <div className="flex gap-2">
                    <button
                      onClick={() => startEditProject(project)}
                      className={`px-3 py-1 rounded-lg border ${t.colors.border} ${t.colors.text} text-sm`}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteProject(project.id!)}
                      className="px-3 py-1 rounded-lg border border-red-400 text-red-300 text-sm"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </section>

          <section className={`p-6 rounded-2xl border ${t.colors.border} ${t.colors.surface} mb-12`}>
            <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
              <div>
                <h2 className={`text-2xl font-bold ${t.colors.highlight}`}>Create new content</h2>
                <p className={t.colors.subtext}>Signed in as admin.</p>
              </div>
              <button
                type="button"
                onClick={handleLogout}
                className={`px-4 py-2 rounded-lg border ${t.colors.border} ${t.colors.text}`}
              >
                Sign out
              </button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <select
                value={form.pageSlug}
                onChange={event => setForm(prev => ({ ...prev, pageSlug: event.target.value }))}
                className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
              >
                {pageOptions.map(option => (
                  <option key={option} value={option}>
                    {option}
                  </option>
                ))}
              </select>
              <input
                value={form.key}
                onChange={event => setForm(prev => ({ ...prev, key: event.target.value }))}
                placeholder="content key (slug)"
                className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
              />
              <input
                value={form.value}
                onChange={event => setForm(prev => ({ ...prev, value: event.target.value }))}
                placeholder="content value"
                className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
              />
              <button
                type="button"
                onClick={handleCreate}
                className={`px-4 py-2 rounded-lg font-bold ${t.colors.accent === 'text-[#89b4fa]' ? 'bg-[#89b4fa] text-[#1e1e2e]' : 'bg-[#1e66f5] text-white'}`}
              >
                Add content
              </button>
            </div>
          </section>

          <section className={`p-6 rounded-2xl border ${t.colors.border} ${t.colors.surface}`}>
            <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
              <h2 className={`text-2xl font-bold ${t.colors.highlight}`}>Existing content</h2>
              <select
                value={filter}
                onChange={event => setFilter(event.target.value)}
                className={`px-3 py-2 rounded-lg ${t.colors.surfaceHighlight} ${t.colors.text} border ${t.colors.border}`}
              >
                <option value="all">All pages</option>
                {pageOptions.map(option => (
                  <option key={option} value={option}>
                    {option}
                  </option>
                ))}
              </select>
            </div>

            {isLoading ? (
              <p className={t.colors.subtext}>Loading content…</p>
            ) : (
              <div className="space-y-4">
                {filteredItems.map(item => (
                  <div
                    key={item.id}
                    className={`p-4 rounded-xl border ${t.colors.border} ${t.colors.surfaceHighlight}`}
                  >
                    {editingId === item.id ? (
                      <div className="grid grid-cols-1 md:grid-cols-4 gap-3">
                        <input
                          value={editForm.pageSlug}
                          onChange={event => setEditForm(prev => ({ ...prev, pageSlug: event.target.value }))}
                          className={`px-3 py-2 rounded-lg ${t.colors.surface} ${t.colors.text} border ${t.colors.border}`}
                        />
                        <input
                          value={editForm.key}
                          onChange={event => setEditForm(prev => ({ ...prev, key: event.target.value }))}
                          className={`px-3 py-2 rounded-lg ${t.colors.surface} ${t.colors.text} border ${t.colors.border}`}
                        />
                        <input
                          value={editForm.value}
                          onChange={event => setEditForm(prev => ({ ...prev, value: event.target.value }))}
                          className={`px-3 py-2 rounded-lg ${t.colors.surface} ${t.colors.text} border ${t.colors.border}`}
                        />
                        <div className="flex gap-2">
                          <button
                            type="button"
                            onClick={handleEditSave}
                            className={`px-4 py-2 rounded-lg font-bold ${t.colors.accent === 'text-[#89b4fa]' ? 'bg-[#89b4fa] text-[#1e1e2e]' : 'bg-[#1e66f5] text-white'}`}
                          >
                            Save
                          </button>
                          <button
                            type="button"
                            onClick={() => setEditingId(null)}
                            className={`px-4 py-2 rounded-lg border ${t.colors.border} ${t.colors.text}`}
                          >
                            Cancel
                          </button>
                        </div>
                      </div>
                    ) : (
                      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-3">
                        <div>
                          <p className={`text-sm uppercase tracking-wider ${t.colors.subtext}`}>{item.pageSlug}</p>
                          <p className={`text-lg font-bold ${t.colors.text}`}>{item.key}</p>
                          <p className={t.colors.subtext}>{item.value}</p>
                        </div>
                        <div className="flex gap-2">
                          <button
                            type="button"
                            onClick={() => handleEditStart(item)}
                            className={`px-4 py-2 rounded-lg border ${t.colors.border} ${t.colors.text}`}
                          >
                            Edit
                          </button>
                          <button
                            type="button"
                            onClick={() => handleDelete(item.id)}
                            className="px-4 py-2 rounded-lg border border-red-400 text-red-300"
                          >
                            Delete
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </section>
        </div>
      </main>

      <Footer data={userData} theme={t} content={content} />
    </div>
  );
}

import { NextResponse } from 'next/server';
import { createSessionToken, setAdminSessionCookie, validateCredentials } from '@/lib/auth';

export const runtime = 'nodejs';

export const POST = async (request: Request) => {
  const payload = (await request.json()) as { username?: string; password?: string };
  const username = payload.username?.trim() ?? '';
  const password = payload.password ?? '';

  if (!username || !password) {
    return NextResponse.json({ error: 'Username and password are required' }, { status: 400 });
  }

  if (!validateCredentials(username, password)) {
    return NextResponse.json({ error: 'Invalid credentials' }, { status: 401 });
  }

  try {
    const token = createSessionToken();
    setAdminSessionCookie(token);
    return NextResponse.json({ ok: true });
  } catch (error) {
    return NextResponse.json({ error: 'Auth configuration missing' }, { status: 500 });
  }
};

import { NextResponse } from 'next/server';

export const runtime = 'nodejs';

const GITHUB_USERNAME = process.env.GITHUB_USERNAME;
const GITHUB_TOKEN = process.env.GITHUB_TOKEN;

export const GET = async () => {
    if (!GITHUB_USERNAME) {
        return NextResponse.json({ error: 'GitHub username not configured' }, { status: 500 });
    }

    try {
        const headers: HeadersInit = {
            'Accept': 'application/vnd.github.v3+json',
            'User-Agent': 'Portfolio-App',
        };

        if (GITHUB_TOKEN) {
            headers['Authorization'] = `Bearer ${GITHUB_TOKEN}`;
        }

        // Fetch user's public events
        const response = await fetch(`https://api.github.com/users/${GITHUB_USERNAME}/events/public?per_page=50`, {
            headers,
            next: { revalidate: 60 } // Cache for 1 minute
        });

        if (!response.ok) {
            throw new Error(`GitHub API error: ${response.status}`);
        }

        const events = await response.json();

        const commits = events
            .map((event: any) => {
                const repoName = event.repo?.name || 'unknown';
                const repoShort = repoName.split('/')[1] || repoName;
                const date = event.created_at;

                // Handle PushEvent
                if (event.type === 'PushEvent') {
                    const commitCount = event.payload?.commits?.length || 0;
                    const lastCommit = commitCount > 0 ? event.payload.commits[commitCount - 1] : null;

                    return {
                        id: event.id,
                        repo: repoShort,
                        message: lastCommit?.message || `Pushed to ${event.payload?.ref?.replace('refs/heads/', '') || 'repository'}`,
                        date: date,
                        url: lastCommit ? `https://github.com/${repoName}/commit/${lastCommit.sha}` : `https://github.com/${repoName}`
                    };
                }

                // Handle Merged Pull Requests
                if (event.type === 'PullRequestEvent' && event.payload.action === 'closed' && event.payload.pull_request?.merged) {
                    return {
                        id: event.id,
                        repo: repoShort,
                        message: `Merged: ${event.payload.pull_request.title}`,
                        date: date,
                        url: event.payload.pull_request.html_url
                    };
                }

                // Handle Repository Creation
                if (event.type === 'CreateEvent' && event.payload.ref_type === 'repository') {
                    return {
                        id: event.id,
                        repo: repoShort,
                        message: `Created repository ${repoShort}`,
                        date: date,
                        url: `https://github.com/${repoName}`
                    };
                }

                return null;
            })
            .filter((item: any) => item !== null)
            .slice(0, 5);

        return NextResponse.json({ commits });
    } catch (error) {
        console.error('Error fetching GitHub commits:', error);
        return NextResponse.json({ commits: [] }, { status: 200 });
    }
};

'use client';

import { useState, useEffect } from 'react';
import { Theme } from '@/lib/types';

interface ClockProps {
  theme: Theme;
  compact?: boolean;
}

export function Clock({ theme, compact = false }: ClockProps) {
  const [time, setTime] = useState<string>('');
  const [date, setDate] = useState<string>('');
  const [timezone, setTimezone] = useState<string>('');
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);

    // Detect user's timezone
    const userTimezone = Intl.DateTimeFormat().resolvedOptions().timeZone;
    setTimezone(userTimezone);

    const updateTime = () => {
      const now = new Date();
      const userTime = new Date(
        now.toLocaleString('en-US', { timeZone: userTimezone })
      );

      const hours = String(userTime.getHours()).padStart(2, '0');
      const minutes = String(userTime.getMinutes()).padStart(2, '0');
      const seconds = String(userTime.getSeconds()).padStart(2, '0');
      
      const day = String(userTime.getDate()).padStart(2, '0');
      const month = String(userTime.getMonth() + 1).padStart(2, '0');
      const year = userTime.getFullYear();

      if (compact) {
        setTime(`${hours}:${minutes}`);
      } else {
        setTime(`${hours}:${minutes}:${seconds}`);
        setDate(`${day}.${month}.${year}`);
      }
    };

    updateTime();
    const interval = setInterval(updateTime, 1000);

    return () => clearInterval(interval);
  }, [compact, timezone]);

  if (!mounted) return null;

  return (
    <div className={`font-mono tracking-wider ${theme.colors.text}`}>
      {compact ? (
        <span className="text-xs">{time}</span>
      ) : (
        <div className="flex flex-col items-center gap-1">
          <span className="text-sm">{time}</span>
          <span className="text-xs opacity-60">{date}</span>
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { GitCommit, Code, Cpu, MapPin, Terminal } from 'lucide-react';
import type { ContentMap, Theme, UserData } from '@/lib/types';
import { SpotlightCard } from './ui/SpotlightCard';

export const Dashboard = ({
  data,
  theme,
  clickCount,
  onCounterClick,
  content,
}: {
  data: UserData;
  theme: Theme;
  clickCount: number;
  onCounterClick: () => void;
  content: ContentMap;
}) => {
  const copy = content.home ?? {};

  // Use local state for immediate feedback, but sync with DB
  const [localClicks, setLocalClicks] = useState(clickCount);
  const [pendingClicks, setPendingClicks] = useState(0);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    const fetchClicks = async () => {
      try {
        const res = await fetch('/api/stats/clicks');
        if (res.ok) {
          const data = await res.json();
          setLocalClicks(data.clicks);
        }
      } catch (e) {
        console.error('Failed to fetch clicks:', e);
      }
    };
    fetchClicks();
  }, []);

  // Debounced save
  useEffect(() => {
    if (pendingClicks === 0) return;

    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }

    timeoutRef.current = setTimeout(async () => {
      const amountToSend = pendingClicks;
      setPendingClicks(0); // Reset pending immediately to avoid double send

      try {
        await fetch('/api/stats/clicks', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ amount: amountToSend }),
        });
      } catch (e) {
        console.error('Failed to update clicks:', e);
      }
    }, 1000); // Wait 1 second after last click

    return () => {
      if (timeoutRef.current) clearTimeout(timeoutRef.current);
    };
  }, [pendingClicks]);

  const [recentCommits, setRecentCommits] = useState<any[]>([]);

  useEffect(() => {
    const fetchCommits = async () => {
      try {
        const res = await fetch('/api/stats/commits');
        if (res.ok) {
          const data = await res.json();
          setRecentCommits(data.commits);
        }
      } catch (e) {
        console.error('Failed to fetch commits:', e);
      }
    };
    fetchCommits();
  }, []);

  const handleClick = () => {
    // Optimistic update
    setLocalClicks(prev => prev + 1);
    setPendingClicks(prev => prev + 1);
    onCounterClick();
  };

  return (
    <section className="opacity-0 animate-fade-in-up" style={{ animationDelay: '500ms' }}>
      <div className="flex items-center gap-3 mb-6 md:mb-10">
        <Terminal size={20} className={`${theme.colors.accent} animate-float md:w-6 md:h-6`} />
        <h2 className={`text-2xl md:text-3xl font-bold font-mono ${theme.colors.highlight}`}>
          {content.global?.dashboardTitle ?? 'Dashboard'}
