}

export const SuccessModal = ({ isOpen, onClose, theme, autoCloseTime = 3 }: SuccessModalProps) => {
    const [countdown, setCountdown] = useState(autoCloseTime);

    useEffect(() => {
        if (isOpen) {
            setCountdown(autoCloseTime);
            const timer = setInterval(() => {
                setCountdown((prev) => {
                    if (prev <= 1) {
                        return 0;
                    }
                    return prev - 1;
                });
            }, 1000);

            return () => clearInterval(timer);
        }
    }, [isOpen, autoCloseTime]);

    useEffect(() => {
        if (isOpen && countdown === 0) {
            onClose();
        }
    }, [isOpen, countdown, onClose]);

    return (
        <AnimatePresence>
            {isOpen && (
                <>
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        onClick={onClose}
                        className="fixed inset-0 z-50 bg-black/40 backdrop-blur-sm flex items-center justify-center p-4"
                    >
                        <motion.div
                            initial={{ scale: 0.8, opacity: 0, y: 20 }}
                            animate={{ scale: 1, opacity: 1, y: 0 }}
                            exit={{ scale: 0.8, opacity: 0, y: 20 }}
                            onClick={(e) => e.stopPropagation()}
                            className={`relative w-full max-w-md p-8 rounded-3xl border shadow-2xl ${theme.colors.surface} ${theme.colors.border} flex flex-col items-center text-center`}
                        >
                            <button
                                onClick={onClose}
                                className={`absolute top-4 right-4 p-2 rounded-full transition-colors hover:bg-black/5 ${theme.colors.subtext}`}
                            >
                                <X size={20} />
                            </button>

                            <motion.div
                                initial={{ scale: 0 }}
                                animate={{ scale: 1, rotate: 360 }}
                                transition={{ type: 'spring', stiffness: 200, damping: 15, delay: 0.2 }}
                                className={`w-20 h-20 rounded-full flex items-center justify-center mb-6 ${theme.colors.surfaceHighlight}`}
                            >
                                <CheckCircle size={40} className="text-green-500" />
                            </motion.div>

                            <motion.h2
                                initial={{ opacity: 0, y: 10 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: 0.3 }}
                                className={`text-2xl font-bold mb-2 ${theme.colors.highlight}`}
                            >
                                Message Sent!
                            </motion.h2>

                            <motion.p
                                initial={{ opacity: 0, y: 10 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: 0.4 }}
                                className={`mb-8 ${theme.colors.subtext}`}
                            >
                                Thanks for reaching out. I'll get back to you as soon as possible.
                            </motion.p>

                            <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-1.5 overflow-hidden mb-2">
                                <motion.div
                                    initial={{ width: '100%' }}
                                    animate={{ width: '0%' }}
                                    transition={{ duration: autoCloseTime, ease: 'linear' }}
                                    className={`h-full ${theme.colors.accent === 'text-[#89b4fa]' ? 'bg-[#89b4fa]' : 'bg-[#1e66f5]'}`}
                                />
                            </div>

                            <p className={`text-xs ${theme.colors.subtext} opacity-60`}>
                                Closing in {countdown}s
                            </p>

                        </motion.div>
                    </motion.div>
                </>
            )}
        </AnimatePresence>
    );
};

import { useEffect, useRef, useState } from 'react';

export function useScrollAnimation() {
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate-fade-in-up');
          entry.target.classList.remove('opacity-0');
          observer.unobserve(entry.target);
        }
      },
      {
        threshold: 0.1,
        rootMargin: '50px',
      }
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    };
  }, []);

  return ref;
}

export function useParallaxScroll() {
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleScroll = () => {
      if (ref.current) {
        const rect = ref.current.getBoundingClientRect();
        const scrolled = window.scrollY;
        const elementOffset = ref.current.offsetTop;
        
        // Parallax effect: move slower than scroll
        const yPos = (scrolled - elementOffset) * 0.5;
        ref.current.style.transform = `translateY(${yPos}px)`;
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return ref;
}

export function useCountUpAnimation(targetNumber: number) {
  const ref = useRef<HTMLSpanElement>(null);
  const [count, setCount] = useState(0);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && count === 0) {
          // Animate count up
          const duration = 2000;
          const steps = 60;
          const increment = targetNumber / steps;
          let current = 0;

          const interval = setInterval(() => {
            current += increment;
            if (current >= targetNumber) {
              setCount(targetNumber);
              clearInterval(interval);
            } else {
              setCount(Math.floor(current));
            }
          }, duration / steps);

          return () => clearInterval(interval);
        }
      },
      { threshold: 0.5 }
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    };
  }, [targetNumber, count]);

  return { ref, count };
}

import type { ContentItem } from '@/lib/types';
import {
  deleteContentItem,
  listContentItems,
  updateContentItem,
  upsertContentItem,
} from '@/lib/services/contentService';

const toItem = (row: { id: number; pageSlug: string; contentKey: string; value: string }): ContentItem => ({
  id: row.id,
  pageSlug: row.pageSlug,
  key: row.contentKey,
  value: row.value,
});

export const handleGetContent = async (request: Request) => {
  const { searchParams } = new URL(request.url);
  const page = searchParams.get('page') ?? undefined;

  try {
    const result = await listContentItems(page);
    return { status: 200, body: { items: result.map(toItem) } };
  } catch (error) {
    return { status: 500, body: { error: 'Database unavailable' } };
  }
};

export const handlePostContent = async (request: Request) => {
  const payload = (await request.json()) as {
    pageSlug?: string;
    key?: string;
    value?: string;
  };

  const pageSlug = payload.pageSlug?.trim();
  const key = payload.key?.trim();
  const value = payload.value?.trim();

  if (!pageSlug || !key || !value) {
    return { status: 400, body: { error: 'pageSlug, key, and value are required' } };
  }

  try {
    const item = await upsertContentItem({ pageSlug, key, value });
    return { status: 201, body: { item: toItem(item) } };
  } catch (error) {
    return { status: 500, body: { error: 'Database unavailable' } };
  }
};

export const handlePutContent = async (request: Request, params: { id: string }) => {
  const { id } = params;
  const payload = (await request.json()) as { pageSlug?: string; key?: string; value?: string };
  const pageSlug = payload.pageSlug?.trim();
  const key = payload.key?.trim();
  const value = payload.value?.trim();

  if (!pageSlug || !key || !value) {
    return { status: 400, body: { error: 'pageSlug, key, and value are required' } };
  }

  try {
    const item = await updateContentItem(Number(id), { pageSlug, key, value });
    return { status: 200, body: { item: toItem(item) } };
  } catch (error) {
    return { status: 404, body: { error: 'Content item not found' } };
  }
};

export const handleDeleteContent = async (params: { id: string }) => {
  const { id } = params;
  try {
    const item = await deleteContentItem(Number(id));
    return { status: 200, body: { item: toItem(item) } };
  } catch (error) {
    return { status: 404, body: { error: 'Content item not found' } };
  }
};

